import React from "react";
import { Link } from "react-router-dom";
import { Button, Card } from "react-bootstrap";
export const ShoppingCart = () => {
  if (props.items.length === 0) {
    return (
      <div className="EmptyCart">
        <Header notMobile={props.notMobile} />
        Nothing in Cart
      </div>)
  } else
    return (
      <div className="shoppingCart" id="shoppingCartScroll" onclick={(e) =>
        props.lockScroll()}>
        <Header notMobile={props.notMobile} />
        <ProductsDisplay items={props.items} checkout={props.checkout} />


      </div>
    );
}


const Product = (props) => {
  const { name, price, wieght, category, description, img } = props;

  return (
    <Card className="my-3 p-3 rounded">
      <Link to={`/product/`}>
        <Card.Img className="product-img" src={img} variant="top" />
      </Link>

      <Card.Body>
        <Link to={`/product/`}>
          <Card.Title as="div">
            <strong>Name: {name}</strong>
          </Card.Title>
        </Link>

        <Card.Text as="div">Weight: {wieght}</Card.Text>
        <Card.Text as="div">Price: {price}</Card.Text>
        <Card.Text as="div">Category: {category}</Card.Text>
        <Card.Text as="div">Description{description}</Card.Text>

        <Link to={"/"}>
          <button className="btn btn-primary">Add to Cart</button>
          <section className="Content" id="Cart" onclick={this.releaseScroll}>
            <div className="TranspBackground"></div>
            <ShoppingCart items={this.state.cart} lockScroll={this.lockScroll} notMobile=
              {this.state.notMobile} checkout={this.checkout}/>
          </section>
        </Link>
      </Card.Body>
    </Card>

  );
};

export default Product;
